import React, { Component } from "react";
class CreateNote extends Component {
  constructor(props) {
    super(props);
    this.state = {
      courseId: "",
      name: ""
    };
  }
  handleChange = e => {
    const value = e.target.value;

    this.setState({
      [e.target.name]: value
    });
    console.log(this.state);
  };
  handleClick = () => {
    if (this.state.course !== "" && this.state.name !== "") {
      const { courseId, name } = this.state;
      let data = { name: name, courseId: courseId };
      console.log(data, "asdfghj");
      this.props.handleSubmit(data);
    }
  };
  render() {
    return (
      <div>
        <p>Add Material</p>
        <select
          onChange={this.handleChange}
          name="courseId"
          value={this.state.courseId}
        >
          {this.props.courses.map(item => (
            <option key={item.id} name="courseId" value={item.title}>
              {item.id}) {item.title}
            </option>
          ))}
        </select>
        <input
          placeholder="Display Name"
          name="name"
          onChange={this.handleChange}
        />
        <button onClick={this.handleClick}>Upload</button>
      </div>
    );
  }
}

export default CreateNote;
