import React, { Component } from "react";
import EachNote from "./components/eachNote/note";
import CallApi from "./api";
import CreateNote from "./createNote.js";

const callApi = new CallApi();
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      courses: [],
      notes: [],
      newNote: "",
      newNoteCourse: ""
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  componentDidMount() {
    var self = this;
    callApi.getCourses().then(function(res) {
      console.log(res);
      self.setState({ courses: res });
    });
  }
  openNotes = e => {
    var self = this;
    callApi.getNotes(e.target.id).then(function(res) {
      console.log(res);
      self.setState({ notes: res });
    });
  };
  submitNote = () => {
    console.log(this.state.newNoteCourse);
    let { newNote, newNoteCourse } = this.state;
    let data = {
      name: newNote,
      course: newNoteCourse,
      no_download: 0
    };
    callApi
      .createNote(data)
      .then(() => {
        console.log("sucess");
      })
      .catch(err => console.log(err));
  };
  async handleSubmit(data) {
    if (data) {
      await this.setState({
        newNote: data.name,
        newNoteCourse: this.state.courses.find(
          item => item.id === Number(data.courseId)
        )
      });
    }

    this.submitNote();
  }

  render() {
    return (
      <div>
        <EachNote title="PDF Title" date="6/11/2019" nodownload="14" />
        <ul>
          {this.state.courses.map(item => (
            <li key={item.id} onClick={this.openNotes} id={item.id}>
              {item.title}
            </li>
          ))}
        </ul>
        {this.state.notes.map(item => (
          <EachNote
            title={item.name}
            date={item.date_uploaded}
            nodownload={item.no_download}
          />
        ))}
        <div>
          <CreateNote
            courses={this.state.courses}
            handleSubmit={this.handleSubmit}
          />
        </div>
      </div>
    );
  }
}

export default App;
