import React, { Component } from "react";
import EachNote from "./components/eachNote/note";
import CallApi from "./api";
import createNote from "./createNote.js";

const callApi = new CallApi();
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      courses: [],
      notes: [],
      newNote: "",
      newNoteCourse: ""
    };
  }
  componentDidMount() {
    var self = this;
    callApi.getCourses().then(function(res) {
      console.log(res);
      self.setState({ courses: res });
    });
  }
  openNotes = e => {
    var self = this;
    callApi.getNotes(e.target.id).then(function(res) {
      console.log(res);
      self.setState({ notes: res });
    });
  };
  // getCourse=()=>{
  //   var course=this.state.courses.filter((item)=>item.id===this.state.newNoteCourse),

  // }
  handleSubmit = data => {
    if (data) {
      return this.setState(
        {
          newNote: data.name,
          newNoteCourse: data.courseId
        },
        () => this.getCourse()
      );
    }
  };
  render() {
    return (
      <div>
        <EachNote title="PDF Title" date="6/11/2019" nodownload="14" />
        <ul>
          {this.state.courses.map(item => (
            <li key={item.id} onClick={this.openNotes} id={item.id}>
              {item.title}
            </li>
          ))}
        </ul>
        {this.state.notes.map(item => (
          <EachNote
            title={item.name}
            date={item.date_uploaded}
            nodownload={item.no_download}
          />
        ))}
        <div>
          <createNote
            // courses={this.state.courses}
            handleSubmit={this.handleSubmit}
          />
        </div>
      </div>
    );
  }
}

export default App;
